mapper.select:
select t1.id, t1.create_by, t1.create_time, t1.update_by, t1.update_time, t1.del_flag, t1.member_id, t1.belong_coach_id, t1.contract_amount, t1.contract_status, t1.use_begin_date, t1.use_end_date, t1.buy_time, t1.contract_remark, t1.actual_charge_amount, t1.course_info_id, t1.course_type, t1.course_use_period_days, t1.course_available_quantity, t1.course_remain_quantity, t1.course_use_quantity, t1.course_total_stop_days, t2.member_name, t3.coach_name as belong_coach_name, t4.course_name from jljs_contract_info t1 left join jljs_member_info t2 on t1.member_id = t2.id left join jljs_coach_info t3 on t1.belong_coach_id = t3.id left join jljs_course_info t4 on t1.course_info_id = t4.id where t1.del_flag = '0'
and t1.member_id = #{criteria.memberId}
and t1.belong_coach_id = #{criteria.belongCoachId}
and t1.contract_status = #{criteria.contractStatus}
and t1.contract_remark like concat('%',#{criteria.contractRemark},'%')
and t1.course_info_id = #{criteria.courseInfoId}
and t1.id = #{criteria.id}
order by t1.id desc
