mapper.select:
WITH tempt AS ( SELECT t1.id, t1.note_id, t1.tag_list, TRIM(JSON_UNQUOTE(JSON_EXTRACT(tag.value, '$'))) AS tag FROM xhs_note AS t1, JSON_TABLE(CONCAT('["', REPLACE(t1.tag_list, ',', '","'), '"]'), '$[*]' COLUMNS (value JSON PATH '$')) AS tag WHERE t1.tag_list IS NOT NULL AND t1.tag_list != '' AND t1.source_keyword = #{keyword} )
SELECT tag, COUNT(DISTINCT note_id) AS occurrenceCount FROM tempt WHERE 1 = 1
AND tag NOT IN (SELECT source_keyword FROM xhs_note st1 WHERE st1.source_keyword IS NOT NULL AND st1.source_keyword != '') GROUP BY tag ORDER BY occurrenceCount DESC LIMIT #{limit};

SELECT count(1) > 0
FROM xhs_note t1
WHERE t1.source_keyword = #{keyword}
